import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Pokemon } from '../../Models/pokemon';
import { PokemonService } from '../../Services/pokemon.service';
import { Subscription } from 'rxjs';


@Component({
  selector: 'app-pokemon-detail',
  templateUrl: './pokemondetail.component.html',
  styleUrls: ['./pokemondetail.component.css']
})
export class PokemonDetailComponent implements OnInit {
  pokemonId!: number;
  pokemon!: Pokemon;
  favoritePokemons: Pokemon[] = [];



  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private pokemonService: PokemonService
  ) { }

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.pokemonId = +params['id'];
      this.loadPokemonDetails();
    });
  }

  loadPokemonDetails() {
    this.pokemonService.getPokemonById(this.pokemonId).subscribe(
      (pokemon: Pokemon) => {
        this.pokemon = pokemon;
      },
      (error) => {
        console.error('Failed to load pokemon details:', error);
      }
    );
  }

  goBack() {
    this.router.navigate(['/pokemons']);
  }

  getPokemonImage(id: number): string {
    return this.pokemonService.getPokemonImage(id);
  }

  addToFavorites(pokemonId: number) {
    this.pokemonService.getPokemonById(pokemonId).subscribe(
      (pokemon: Pokemon) => {
        if (!this.favoritePokemons.includes(pokemon)) {
          this.favoritePokemons.push(pokemon);
        }
      },
      (error) => {
        console.error('Failed to add pokemon to favorites:', error);
      }
    );
  }

}
<div class="pokemon-detail">
  <h1>{{ pokemon.name }}</h1>
  <img [src]="getPokemonImage(pokemon.id)" [alt]="pokemon.name" />
  <p>ID: {{ pokemon.id }}</p>
  <p>Height: {{ pokemon.height }} m</p>
  <p>Weight: {{ pokemon.weight }} kg</p>
  <p>Family: {{ pokemon.family }}</p>
  <div class="types">
    <span *ngFor="let type of pokemon.types" [class]="'type-' + type.toLowerCase()" [style]="type | pokemonTypeBackground">{{ type }}</span>
  </div>
  <button class="favorite-button" (click)="addToFavorites(pokemon)">Add to Favorites</button>
  <button class="back-button" (click)="goBack()">Back</button>
</div>
<div *ngFor="let pokemon of filteredPokemons" [routerLink]="['/pokemon', pokemon.id]">
  <div class="pokemon-card" [ngClass]="'type-' + pokemon.types[0].toLowerCase()">
    <!-- other pokemon details -->
    <button *ngIf="!showFavorites" class="favorite-button" (click)="addToFavorites(pokemon.id)">Add to Favorites</button>
  </div>
</div>
