import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { Pokemon } from '../Models/pokemon';

@Injectable({
  providedIn: 'root'
})
export class PokemonService {
  private url: string = 'https://softwium.com/api/pokemons';

  constructor(private http: HttpClient) { }

  getAllPokemons(page: number = 1, limit: number = 20): Observable<Pokemon[]> {
    const offset = (page - 1) * limit;
    const url = `${this.url}pokemon?offset=${offset}&limit=${limit}`;
    return this.http.get<any>(url).pipe(
      map((data: any) => {
        this.getAllPokemons = data.count;
        return data.results.map((result: any) => {
          return {
            id: result.id,
            name: result.name,
            height: result.height,
            weight: result.weight,
            types: result.types.map((type: any) => type.name),
            family: result.family // ajuste conforme sua lógica de negócios
          };
        });
      })
    );
  }

  getPokemonImage(id: number): string {
    return `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`;
  }
}
