import { Component } from '@angular/core';
import { Pokemon } from '../../Models/pokemon';
import { PokemonService } from '../../Services/pokemon.service';

@Component({
  selector: 'app-battles',
  templateUrl: './battles.component.html',
  styleUrls: ['./battles.component.css']
})
export class BattlesComponent {
  pokemonA: Pokemon | null = null;
  pokemonB: Pokemon | null = null;
  filteredPokemonListA: Pokemon[] = [];
  filteredPokemonListB: Pokemon[] = [];
  battleResult: string | null = null;

  constructor(public pokemonService: PokemonService) { }

  filterPokemonA(searchTerm: string): void {
    this.pokemonService.getAllPokemons().subscribe(pokemons => {
      this.filteredPokemonListA = pokemons.filter(pokemon =>
        pokemon.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
  }

  filterPokemonB(searchTerm: string): void {
    this.pokemonService.getAllPokemons().subscribe(pokemons => {
      this.filteredPokemonListB = pokemons.filter(pokemon =>
        pokemon.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
  }

  selectPokemonA(pokemon: Pokemon): void {
    this.pokemonA = pokemon;
  }

  selectPokemonB(pokemon: Pokemon): void {
    this.pokemonB = pokemon;
  }

  simulateBattle(): void {
    if (this.pokemonA && this.pokemonB) {
      // Simulate battle logic here
      this.battleResult = `${this.pokemonA.name} vs ${this.pokemonB.name}: Winner - ${Math.random() < 0.5 ? this.pokemonA.name : this.pokemonB.name}`;
    } else {
      this.battleResult = 'Please select both Pokemon before simulating the battle.';
    }
  }
  //private filterPokemonList(query: string): Pokemon[] {
  //  if (query.trim() === '') {
  //    return [];
  //  }
  //  return this.pokemonList.filter(pokemon =>
  //    pokemon.name.toLowerCase().includes(query.toLowerCase()) ||
  //    pokemon.id.toString().includes(query)
  //  );
  //}

}
<div class="battles-container">
  <h1>Pokémon Battles</h1>
  <div>
    <label for="pokemonA">Pokemon A:</label>
    <input type="text" id="pokemonA" [(ngModel)]="pokemonA" (input)="filterPokemonA($event.target.value)">
    <div class="pokemon-list">
      <div *ngFor="let pokemon of filteredPokemonListA" class="pokemon-item" (click)="selectPokemonA(pokemon)">
        <img [src]="pokemonService.getPokemonImage(pokemon.id)" alt="{{ pokemon.name }}"> {{ pokemon.name }}
      </div>
    </div>
  </div>
  <div>
    <label for="pokemonB">Pokemon B:</label>
    <input type="text" id="pokemonB" [(ngModel)]="pokemonB" (input)="filterPokemonB($event.target.value)">
    <div class="pokemon-list">
      <div *ngFor="let pokemon of filteredPokemonListB" class="pokemon-item" (click)="selectPokemonB(pokemon)">
        <img [src]="pokemonService.getPokemonImage(pokemon.id)" alt="{{ pokemon.name }}"> {{ pokemon.name }}
      </div>
    </div>
  </div>
  <button (click)="simulateBattle()">Simulate Battle</button>
  <div *ngIf="battleResult" class="battle-result">
    <h2>Battle Result:</h2>
    <p>{{ battleResult }}</p>
  </div>
</div>
