// pokedex.component.ts
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Pokemon, PokemonService } from '../../Services/pokemon.service';

@Component({
  selector: 'app-pokedex',
  templateUrl: './pokedex.component.html',
  styleUrls: ['./pokedex.component.css']
})
export class PokedexComponent implements OnInit {
  pokemons: Pokemon[] = [];
  nextUrl: string | null = null;

  constructor(private router: Router, private pokemonService: PokemonService) {}

  loadDashboard() {
    this.router.navigate(['/dashboard']);
  }

  ngOnInit(): void {
    this.getAllPokemons();
  }

  getAllPokemons(): void {
    this.pokemonService.getAllPokemons()
      .subscribe((data: any) => {
        this.pokemons = data.results;
      });
  }

  loadMore(): void {
    if (this.nextUrl) {
      this.getAllPokemons(); // Chama getAllPokemons() novamente para carregar mais pokemons
    }
  }
}
<!-- pokedex.component.html -->
<div>
  <h1>Pokedex</h1>
  <button (click)="loadDashboard()">Go to Dashboard</button>

  <div *ngIf="pokemons.length > 0">
    <ul>
      <li *ngFor="let pokemon of pokemons">
        {{ pokemon.name }}
      </li>
    </ul>
  </div>
  <div *ngIf="pokemons.length === 0">
    Loading...
  </div>
</div>
