import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { Pokemon } from '../Models/pokemon';

@Injectable({
  providedIn: 'root'
})
export class PokemonService {
  private baseUrl = 'https://pokeapi.co/api/v2/';

  constructor(private http: HttpClient) { }

  getAllPokemons(offset: number = 0, limit: number = 10): Observable<Pokemon> {
    const listaPokemonsURL = `${this.baseUrl}pokemon?offset=${offset}&limit=${limit}`;

    return this.http.get<any>(listaPokemonsURL).pipe(
      map(data => data.results.map((pokemon: {
        url: string,
        name: string
      }) => {
        console.log(pokemon)
        return {
          ...pokemon
        };
      })),
    );
  }
}
