import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map, mergeMap } from 'rxjs/operators';
import { Pokemon } from '../Models/pokemon';

@Injectable({
  providedIn: 'root'
})
export class PokemonService {
  private baseUrl = 'https://pokeapi.co/api/v2/';

  constructor(private http: HttpClient) { }

  getAllPokemons(offset: number = 0, limit: number = 10): Observable<Pokemon[]> {
    const listaPokemonsURL = `${this.baseUrl}pokemon?offset=${offset}&limit=${limit}`;

    return this.http.get<any>(listaPokemonsURL).pipe(
      mergeMap((data: any) => {
        const pokemons = data.results.map((pokemon: { url: string, name: string }) => {
          return { name: pokemon.name, url: pokemon.url, sprite: null }; // Inicialmente, a sprite é null
        });
        const pokemonRequests = pokemons.map((pokemon: Pokemon) => {
          return this.http.get<any>(pokemon.url).pipe(
            map((pokemonDetails: any) => {
              pokemon.sprite = pokemonDetails.sprites.front_default;
              return pokemon;
            })
          );
        });
        return forkJoin(pokemonRequests);
      })
    );
  }
}
