import { Component } from '@angular/core';
import { PokemonService } from '../../Services/pokemon.service';
import { Pokemon } from '../../Models/pokemon';

@Component({
  selector: 'app-battles',
  templateUrl: './battles.component.html',
  styleUrls: ['./battles.component.css']
})
export class BattlesComponent {
  pokemonA: string = '';
  pokemonB: string = '';
  battleResult: string = '';
  pokemonList: Pokemon[] = [];
  filteredPokemonListA: Pokemon[] = [];
  filteredPokemonListB: Pokemon[] = [];
  battleResultImage: string = ''; // Declare this at the top of your component class


  constructor(private pokemonService: PokemonService) { }

  ngOnInit(): void {
    this.pokemonService.getAllPokemons().subscribe(
      (pokemons: Pokemon[]) => {
        this.pokemonList = pokemons;
      }
    );
  }

  filterPokemonA(query: string): void {
    this.filteredPokemonListA = this.filterPokemonList(query);
  }

  filterPokemonB(query: string): void {
    this.filteredPokemonListB = this.filterPokemonList(query);
  }

  private filterPokemonList(query: string): Pokemon[] {
    if (query.trim() === '') {
      return [];
    }
    return this.pokemonList.filter(pokemon =>
      pokemon.name.toLowerCase().includes(query.toLowerCase()) ||
      pokemon.id.toString().includes(query)
    );
  }

  selectPokemonA(pokemon: Pokemon): void {
    this.pokemonA = pokemon.name;
    this.filteredPokemonListA = [];
  }

  selectPokemonB(pokemon: Pokemon): void {
    this.pokemonB = pokemon.name;
    this.filteredPokemonListB = [];
  }

  simulateBattle(): void {
    // Simulate battle logic (e.g., coin flip)
    const winner = Math.random() < 0.5 ? this.pokemonA : this.pokemonB;
    const winnerId = winner === this.pokemonA ? this.filteredPokemonListA.find(p => p.name === winner)?.id : this.filteredPokemonListB.find(p => p.name === winner)?.id;
    this.battleResult = `${winner} wins the battle!`;
    this.battleResultImage = this.getPokemonImage(winnerId || 0);
  }


  getPokemonImage(id: number): string {
    return `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`;
  }

}
<div>
  <h1>Pokémon Battles</h1>
  <div>
    <label for="pokemonA">Pokemon A:</label>
    <input type="text" id="pokemonA" [(ngModel)]="pokemonA" (input)="filterPokemonA(pokemonA)">
    <div class="pokemon-list">
      <div *ngFor="let pokemon of filteredPokemonListA" (click)="selectPokemonA(pokemon)">
        <img [src]="getPokemonImage(pokemon.id)" alt="{{ pokemon.name }}">
        {{ pokemon.name }}
      </div>
    </div>
  </div>
  <div>
    <label for="pokemonB">Pokemon B:</label>
    <input type="text" id="pokemonB" [(ngModel)]="pokemonB" (input)="filterPokemonB(pokemonB)">
    <div class="pokemon-list">
      <div *ngFor="let pokemon of filteredPokemonListB" (click)="selectPokemonB(pokemon)">
        <img [src]="getPokemonImage(pokemon.id)" alt="{{ pokemon.name }}">
        {{ pokemon.name }}
      </div>
    </div>
  </div>
  <button (click)="simulateBattle()">Simulate Battle</button>
  <div *ngIf="battleResult">
    <h2>Battle Result:</h2>
    <p>{{ battleResult }}</p>
  </div>
</div>
