import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { PokemonService } from '../../Services/pokemon.service';
import { Pokemon } from '../../Models/pokemon';

@Component({
  selector: 'app-pokedex',
  templateUrl: './pokedex.component.html',
  styleUrls: ['./pokedex.component.css']
})
export class PokedexComponent implements OnInit {
  pokemons: Pokemon[] = [];
  filteredPokemons: Pokemon[] = [];
  searchQuery: string = '';
  showFavorites: boolean = false;
  favoritePokemons: Pokemon[] = [];
  currentPage: number = 1;
  itemsPerPage: number = 30;

  constructor(private router: Router, private pokemonService: PokemonService) { }

  loadDashboard() {
    this.router.navigate(['/dashboard']);
  }

  ngOnInit(): void {
    this.pokemonService.getAllPokemons().subscribe(
      (pokemons: Pokemon[]) => {
        this.pokemons = pokemons;
        this.filteredPokemons = pokemons;
      }
    );
    this.loadFavorites();


  }
  
  searchPokemon() {
    console.log("SQ " + this.searchQuery);
    if (!this.searchQuery.trim()) {
      this.filteredPokemons = this.pokemons;
      return;
    }

    const searchResult = this.pokemons.filter(pokemon =>
      pokemon.name.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
      pokemon.id.toString().includes(this.searchQuery)
    );

    this.filteredPokemons = searchResult;
    this.currentPage = 1; // Reset current page when searching
  }

  getPokemonImage(id: number): string {
    return this.pokemonService.getPokemonImage(id);
  }

  toggleFavoritesMode(): void {
    if (this.showFavorites) {
      this.filteredPokemons = [...this.pokemons];
    } else {
      this.filteredPokemons = this.pokemons.filter(pokemon => pokemon.favorite);
    }
    this.currentPage = 1; // Reset current page when toggling favorites mode
    this.showFavorites = !this.showFavorites;
  }

  onMouseEnter(pokemon: any): void {
    if (!this.showFavorites && !pokemon.favorite) {
      pokemon.showFavoriteButton = true;
    }
  }

  onMouseLeave(): void {
    this.filteredPokemons.forEach(pokemon => {
      pokemon.showFavoriteButton = false;
    });
  }

  addToFavorites(pokemon: Pokemon) {
    this.pokemonService.addToFavorites(pokemon);
    this.favoritePokemons = this.pokemonService.getAllFavorites();
  }

  removeFromFavorites(pokemon: Pokemon) {
    this.pokemonService.removeFromFavorites(pokemon);
    this.favoritePokemons = this.pokemonService.getAllFavorites();
  }
  
  viewPokemonDetails(pokemonId: number): void {
    this.router.navigate(['/pokemon', pokemonId]);
  }

  loadFavorites() {
    this.favoritePokemons = this.pokemonService.getAllFavorites();
  }

  nextPage(): void {
    if (this.currentPage < this.getTotalPages()) {
      this.currentPage++;
    }
  }

  prevPage(): void {
    if (this.currentPage > 1) {
      this.currentPage--;
    }
  }

  isFirstPage(): boolean {
    return this.currentPage === 1;
  }

  isLastPage(): boolean {
    return this.currentPage === this.getTotalPages();
  }

  getTotalPages(): number {
    return Math.ceil(this.filteredPokemons.length / this.itemsPerPage);
  }

  getPageItems(): Pokemon[] {
    const startIndex = (this.currentPage - 1) * this.itemsPerPage;
    return this.filteredPokemons.slice(startIndex, startIndex + this.itemsPerPage);
  }

}

<div class="pokedex">
  <h1>Pokedex</h1>
  <div class="button-container">
    <button class="dashboard-button" (click)="loadDashboard()">Dashboard</button>
    <div class="button-margin"></div>
    <button class="favorites-button" (click)="toggleFavoritesMode()">
      {{ showFavorites ? 'Show All Pokemons' : 'Show Favorites' }}
    </button>
  </div>
  <p></p>

  <div class="search-bar">
    <input type="text" placeholder="Search..." [(ngModel)]="searchQuery" (input)="searchPokemon()">
  </div>
  <p></p>

  <div class="pokemon-card-list">
    <div *ngFor="let pokemon of getPageItems()">
      <div class="pokemon-card" [ngClass]="'type-' + pokemon.types[0].toLowerCase()" (click)="viewPokemonDetails(pokemon.id)">
        <img [src]="getPokemonImage(pokemon.id)" [alt]="pokemon.name" />
        <h2>{{ pokemon.name }}</h2>
        <p>ID: {{ pokemon.id }}</p>
        <div class="types">
          <span *ngFor="let type of pokemon.types" [class]="'type-' + type.toLowerCase()" [style]="type | pokemonTypeBackground">{{ type }}</span>
        </div>
        <button class="favorite-button" (click)="$event.stopPropagation(); addToFavorites(pokemon)">
          Add to Favorites
        </button>
        <div class="card-content" (click)="viewPokemonDetails(pokemon.id)">
        </div>
      </div>
    </div>
  </div>


  <div class="page-button-container" *ngIf="!showFavorites">
    <!-- Hide when viewing favorites -->
    <button class="page-button" (click)="prevPage()" [disabled]="isFirstPage()">Previous</button> &nbsp;&nbsp;
    <button class="page-button" (click)="nextPage()" [disabled]="isLastPage()">Next</button>
  </div>
  <div *ngIf="!showFavorites">
    <!-- Hide when viewing favorites -->
    <span class="page-indicator">{{ currentPage }} / {{ getTotalPages() }}</span>
  </div>
</div>


<div class="favorites-container" *ngIf="showFavorites && favoritePokemons.length > 0">
  <div *ngFor="let pokemon of favoritePokemons">
    <div class="pokemon-card" [ngClass]="'type-' + pokemon.types[0].toLowerCase()" (click)="viewPokemonDetails(pokemon.id)">
      <img [src]="getPokemonImage(pokemon.id)" [alt]="pokemon.name" />
      <h2>{{ pokemon.name }}</h2>
      <p>ID: {{ pokemon.id }}</p>
      <p>Height: {{ pokemon.height }} m</p>
      <p>Weight: {{ pokemon.weight }} kg</p>
      <div class="types">
        <span *ngFor="let type of pokemon.types" [class]="'type-' + type.toLowerCase()" [style]="type | pokemonTypeBackground">{{ type }}</span>
      </div>
      <button class="remove-favorite-button" (click)="removeFromFavorites(pokemon)">Remove from Favorites</button>
    </div>
  </div>
</div>
