import { Component, OnInit } from '@angular/core';
import { UserService } from '../../services/user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  userInfo: any;
  newUsername: string = '';
  newPassword: string = '';

  constructor(private userService: UserService,
  private router: Router) { }

  ngOnInit(): void {
    this.userInfo = this.userService.getUserInfo();
  }

  changeUsername() {
    this.userService.changeUsername(this.newUsername);
    this.newUsername = ''; // Limpar o campo de entrada
    this.userInfo = this.userService.getUserInfo(); // Atualizar as informações do usuário exibidas
  }

  changePassword() {
    this.userService.changePassword(this.newPassword);
    this.newPassword = ''; // Limpar o campo de entrada
    this.userInfo = this.userService.getUserInfo(); // Atualizar as informações do usuário exibidas
  }
   openPokedex() {
    this.router.navigate(['/pokemons']);
  }
}
<div class="dashboard-container">
  <h2>DASHBOARD</h2>
  <div>
    <h3>User information:</h3>
    <p><strong>Username:</strong> {{ userInfo.username }}</p>
    <p><strong>Password:</strong> {{ userInfo.password }}</p>
  </div>
  <div>
    <h3>Change username:</h3>
    <input type="text" [(ngModel)]="newUsername" placeholder="New username">
    <button (click)="changeUsername()">Change</button>
  </div>
  <div>
    <h3>Change password:</h3>
    <input type="password" [(ngModel)]="newPassword" placeholder="New password">
    <button (click)="changePassword()">Change</button>
    <p></p>
    <button (click)="openPokedex()">Open PokeDEX</button>

  </div>
</div>
