import { Component } from '@angular/core';

@Component({
  selector: 'app-battles',
  templateUrl: './battles.component.html',
  styleUrls: ['./battles.component.css']
})
export class BattlesComponent {
  pokemonA: string = '';
  pokemonB: string = '';
  battleResult: string = '';

  simulateBattle(): void {
    // Simulate battle logic (e.g., coin flip)
    const winner = Math.random() < 0.5 ? this.pokemonA : this.pokemonB;
    this.battleResult = `${winner} wins the battle!`;
  }
}
<div>
  <h1>Pokémon Battles</h1>
  <div>
    <label for="pokemonA">Pokemon A:</label>
    <input type="text" id="pokemonA" [(ngModel)]="pokemonA">
  </div>
  <div>
    <label for="pokemonB">Pokemon B:</label>
    <input type="text" id="pokemonB" [(ngModel)]="pokemonB">
  </div>
  <button (click)="simulateBattle()">Simulate Battle</button>
  <div *ngIf="battleResult">
    <h2>Battle Result:</h2>
    <p>{{ battleResult }}</p>
  </div>
</div>
